import React, { useEffect, useState } from 'react';
import data from '../../data/cricketers info.json'
import './Crickter.css';
import Card from '../Card/card'

const Crickter = () => {
    const information = data;
    const [cricketers, setCricketers] = useEffect(information);
    const [selected, setSelected] = useState([]);
    const [name, setName] = useState([]);
    
const handleAddCrickter = (crickter) =>{
    const newSelected=[...selected, crickter];
    const newName=[...name, crickter]
    setName(newName);
   setSelected(newSelected);

}

    return (
        <div className="Total-Container">
            <div className="crickter-container">
                {
                    cricketers.map(cricketer => <Card
                        handleAddCrickter={handleAddCrickter}
                        card={cricketer}></Card>)
                }
            </div>
            <div className="selected-container">
                <h3>Selected: {selected.length}</h3>
                <h2>Name: {information.name}</h2>
             
            </div>
        </div>
    );
};

export default Crickter;
